mapper.insert:

INSERT	INTO	teamadonation(
					userid,
					content_id,
					donation
				)
				VALUES(
					#{userid},
					#{content_id},
					#{donation}
				)

mapper.update:

UPDATE	teamadonation
SET		userid = #{userid},
		content_id = #{content_id},
		donation = #{donation}
WHERE	userid = #{userid} AND content_id = #{content_id}

mapper.select:
SELECT userid, content_id, donation, don_reg_date
FROM (SELECT /*+INDEX_DESC(teamadonation XPKTEAMADONATION) */ rownum rn, userid, content_id, donation, don_reg_date FROM teamadonation, WHERE
rownum <= #{pageNum} * #{amount}
) WHERE rn > (#{pageNum} -1) * #{amount}

mapper.delete:
DELETE	FROM	teamadonation
		WHERE	userid = #{userid}
		AND		content_id = #{content_id}
